<?php/*	Plugin Name: AdSense Control	Plugin URI: http://opreh.com.br/	Description: Controls how the post will show up ads and also releases for the visitors from Google	Version: 0.1	Author: Xopx	Author URI: http://opreh.com.br/*/	define("AC_VERSION","1.0");	require_once("meta-box-ac.php");			function ac_search_is_the_reference(){		  $ref = $_SERVER['HTTP_REFERER'];		  $SE = array('/search?','.google.', 'images.google.', 'web.info.com', 'search.', 'del.icio.us/search', 'soso.com', '/search/', '.yahoo.');		  foreach ($SE as $source) {			if (strpos($ref,$source)!==false) return true;		  }		  return false;	}	function ac_get_nivel(){		global $post;		return get_post_meta($post->ID, 'ac_meta_box_active', true);	}		function ac_check(){		$nivel = ac_get_nivel();		switch($nivel){			case "0":				return false;			break;			case "1":				return true;			break;			case "2":				if(ac_search_is_the_reference()){					return true;				}				return false;			break;			case "3":				if(!ac_search_is_the_reference()){					return true;				}				return false;			break;		}				}?>